import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import UserDashboard from './components/UserDashboard';
import UserForm from './components/UserForm';

function App() {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  const addOrUpdateUser = (user) => {
    const updatedUsers = users.map((u) =>
      u.id === user.id ? user : u
    );
    if (!updatedUsers.some((u) => u.id === user.id)) {
      updatedUsers.push(user);
    }
    setUsers(updatedUsers);
  };

  return (
    <Router>
      <div className="app">
        <nav>
          <Link to="/">Dashboard</Link>
          <Link to="/add">Add User</Link>
        </nav>

        <Routes>
          <Route path="/" element={<UserDashboard users={users} />} />
          <Route path="/add" element={<UserForm onSave={addOrUpdateUser} />} />
          <Route path="/edit/:id" element={<UserForm onSave={addOrUpdateUser} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
