import React, { useState, useEffect } from 'react';
import './Dashboard.css';

function UserDashboard() {
  const [users, setUsers] = useState([]);
  const [formData, setFormData] = useState({ firstName: '', lastName: '', department: '' });
  const [isEditing, setIsEditing] = useState(false);
  const [currentUserId, setCurrentUserId] = useState(null);

  // Fetch users from the API
  useEffect(() => {
    fetchUsers();
  }, []);

  // Fetch user data from JSONPlaceholder
  const fetchUsers = async () => {
    try {
      const response = await fetch('https://jsonplaceholder.typicode.com/users');
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.error('Error fetching users:', error);
    }
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  // Add or Edit user
  const handleSubmit = async (e) => {
    e.preventDefault();
    if (isEditing) {
      // Edit user
      const updatedUser = { ...formData, id: currentUserId };
      await updateUser(updatedUser);
    } else {
      // Add new user
      const newUser = { ...formData, id: users.length + 1 };
      await addUser(newUser);
    }
    resetForm();
  };

  // Add a new user
  const addUser = async (user) => {
    setUsers([...users, user]);
  };

  // Update an existing user
  const updateUser = async (updatedUser) => {
    setUsers(users.map(user => user.id === updatedUser.id ? updatedUser : user));
  };

  // Handle edit button click
  const handleEdit = (user) => {
    setFormData({ firstName: user.firstName, lastName: user.lastName, department: user.department });
    setIsEditing(true);
    setCurrentUserId(user.id);
  };

  // Handle delete button click
  const handleDelete = async (userId) => {
    setUsers(users.filter(user => user.id !== userId));
  };

  // Reset the form for add or after editing
  const resetForm = () => {
    setFormData({ firstName: '', lastName: '', department: '' });
    setIsEditing(false);
  };

  return (
    <div className="dashboard-container">
      <div className="form-container">
        <h2>{isEditing ? 'Edit User' : 'Add New User'}</h2>
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="firstName"
            value={formData.firstName}
            onChange={handleInputChange}
            placeholder="First Name"
            required
          />
          <input
            type="text"
            name="lastName"
            value={formData.lastName}
            onChange={handleInputChange}
            placeholder="Last Name"
            required
          />
          <input
            type="text"
            name="department"
            value={formData.department}
            onChange={handleInputChange}
            placeholder="Department"
            required
          />
          <button type="submit">{isEditing ? 'Update' : 'Add'} User</button>
        </form>
      </div>

      <div className="user-list">
        <h2>User List</h2>
        {users.map(user => (
          <div key={user.id} className="user-card">
            <p><strong>Name:</strong> {user.firstName} {user.lastName}</p>
            <p><strong>Department:</strong> {user.department}</p>
            <div className="actions">
              <button onClick={() => handleEdit(user)}>Edit</button>
              <button onClick={() => handleDelete(user.id)}>Delete</button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default UserDashboard;
